import React from "react";
import ColorBox from "./ColorBox";

const ColorArea = (props) => {
  return (
    <div>
      {props.colors.map((color) => (
        <ColorBox color={color.toHexString()} />
      ))}
    </div>
  );
};

export default ColorArea;
