import React, { useState } from "react";
import ColorArea from "./ColorArea";

var tinycolor = require("tinycolor2");

const ColorSelector = () => {
  const [color, setColor] = useState("");
  const [colors, setColors] = useState([]);
  const [scheme, setScheme] = useState("analagous");
  const handleChange = (evt) => {
    setColor(evt.target.value);
  };

  const handleSubmit = (evt) => {
    evt.preventDefault();
    setColors(tinycolor(color).analogous());
  };
  const handleClick = () => {
    console.log("clicked");
    let randomColor = "#" + Math.floor(Math.random() * 16777215).toString(16);
    setColors(tinycolor(randomColor).analogous());
  };
  return (
    <div>
      <button type="button" onClick={handleClick}>
        Generate Random Color Scheme
      </button>
      <form onSubmit={handleSubmit} onChange={handleChange}>
        <label htmlFor="input">Enter Your Base Color:</label>
        <input type="text" value={color} required />
        <button type="submit">Generate Color Scheme</button>
      </form>
      <ColorArea colors={colors} />
    </div>
  );
};

export default ColorSelector;
